        private string  getTheData()
        {
            int i = 1;
            int begin, moviebegin;
            begin = moviebegin = 0;

            thedata = GetMovieData();
            
            //o = ns.JsonConvert.DeserializeObject(thedata);
            List<Movie> themovies = ns.JsonConvert.DeserializeObject<List<Movie>>(thedata);

            List<MovieShowTimes> x = (themovies.Select(movie => new MovieShowTimes() {  title = movie.title
                , showtimes = movie.showtimes, runTime = movie.runTime })).ToList();
            //var showtimes = themovies.SelectMany(movie => new MovieShowTime() { title = movie.title, showtimes = movie.showtimes });

            //List<Showtime> sst =  (showtimes.OrderBy(showtime => showtime.dateTime)).ToList();
            List<TimesWithNameTheater> y = new List<TimesWithNameTheater>();
            
            foreach (Movie m in themovies)
            {
                foreach (Showtime st in m.showtimes)
                {
                    TimesWithNameTheater twnt = new TimesWithNameTheater();
                    twnt.datetime = st.dateTime;
                    twnt.theTheatre = st.theatre.name;
                    twnt.title = m.title;
                    if (String.IsNullOrEmpty(m.runTime) == false)
                    {
                        twnt.runTime = m.runTime.Substring(2, 2) + ":" + m.runTime.Substring(5, 2);
                    }
                    else
                    {
                        twnt.runTime = "???";
                    }
                    
                    y.Add(twnt);
                }
            }

            List<TimesWithNameTheater> zz = y.OrderBy(mt => mt.datetime).ToList();

            StringBuilder sb = new StringBuilder();
            
            sb.Append("<table rules='all' border='1'>");
            sb.Append("<tr><th>cnt</th><th>time</th><th>rt</th><th width=400>movie</th><th>theater</th></tr>");
            

            foreach (TimesWithNameTheater xx in zz)
            {
                string moviestarttime = xx.datetime.Substring(11);

                if (String.IsNullOrEmpty(this.begintime.Value) == false)
                {
                    begin = Convert.ToInt32(this.begintime.Value);
                    moviebegin = Convert.ToInt32(moviestarttime.Substring(0, 2));

                    //ignore entry if moviebegin time is before time user is interested in
                    if (moviebegin < begin) continue;
                
                }

                
                //is there a duration specified?
                if (String.IsNullOrEmpty(this.dur.Value) == false)
                {
                    //yes....ignore entry if moviebegin time is after time user is interested in
                    int end = begin + Convert.ToInt32(this.dur.Value);
                    if (moviebegin > end) continue;
                }

                //
                if (String.IsNullOrEmpty(this.startWith.Value) == false)
                {
                    if (xx.title.ToLower().StartsWith(this.startWith.Value) == false) continue;
                }

                string aline = String.Format("<tr><td>{0}</td><td>{1}</td><td>{2}</td><td>{3}</td><td>{4}</td></tr>",
                    i.ToString(), moviestarttime, xx.runTime, xx.title, xx.theTheatre);
                sb.Append(aline);
                i++;
            }
            
            sb.Append("</table>");
            
            return sb.ToString();
                        

        }

        private string GetMovieData()
        {
            string rawJson;

            try
            {
                string connStr = ConfigurationManager.ConnectionStrings["MovieTimesConnectionString"].ConnectionString;
                SqlConnection conn = new SqlConnection(connStr);
                conn.Open();
                string sql = String.Format("select jsonData from rawJsonData where viewDate = '{0}' and viewZip = '{1}'",
                    this.viewdate.Value, this.viewzip.Value);
                SqlCommand cmd = new SqlCommand(sql, conn);
                //SqlDataAdapter da = new SqlDataAdapter(sql, conn);
                //DataTable dt = new DataTable();
                //int rc1 = da.Fill(dt);

                Object o = cmd.ExecuteScalar();
                rawJson = "";
                if (o != null) rawJson = o.ToString();

                if (String.IsNullOrEmpty(rawJson) == true)
                {
                    rawJson = GetDataFromWeb();
                    sql = String.Format("insert into rawJsonData(viewDate, viewZip, jsonData) values('{0}', '{1}', '{2}')",
                        this.viewdate.Value, this.viewzip.Value, rawJson);
                    //sql = "insert into rawJsonData(viewDate, viewZip, jsonData) values('@viewdate', '@viewzip', '@rawJson')";
                    cmd.CommandText = sql;
                    cmd.CommandType = CommandType.Text;
                    //cmd.Parameters.AddWithValue("@viewdate", this.viewdate.Value);
                    //cmd.Parameters.AddWithValue("@viewzip", this.viewzip.Value);
                    //cmd.Parameters.AddWithValue("@rawJson", rawJson);

                    cmd.ExecuteNonQuery();

                }
            }
            catch (Exception ex)
            {
                string x = ex.Message + "," + ex.StackTrace;
                throw;
            }

            return rawJson;

        }

